/* --------------------------------------------------
 * Client Resources - Accordion component
 * --------------------------------------------------
 */
{
  cy.get(cy_selectors.south.accordion_component).scrollIntoView();
  // Client Resources (Caption and Header)
  cy.get(cy_selectors.south.accordion_component_caption).should("have.text", acm_constants.south.accordion_component_caption);
  cy.get(cy_selectors.south.accordion_component_header).should("have.text", acm_constants.south.accordion_component_header);
  cy.get(cy_selectors.south.accordion_component_load_more).should("have.text", acm_constants.south.accordion_component_load_more);
  cy.wait(1000);
  cy.get(cy_selectors.south.accordion_component_load_more).click({
    force: true
  });
  cy.wait(1000);
  /* 
    First close the first expanded tab. Then for each tab, expand the tab and
    confirm the contents within each tab
    */
  cy.get(cy_selectors.south.client_resources_expanded_tab).scrollIntoView({
    offset: {
      top: -60,
      left: 0
    }
  }).find(cy_selectors.south.client_resources_panel_header);
  //.click({ scrollBehavior: false });
  cy.get(cy_selectors.south.client_resources_regular_tab).each(($ele, panelIndex) => {
    cy.wrap($ele).find(cy_selectors.south.client_resources_panel_header).scrollIntoView({
      offset: {
        top: -80,
        left: 0
      }
    }).click({
      scrollBehavior: false
    });
    // Confirm the tab text
    cy.wrap($ele).find(cy_selectors.south.client_resources_panel_header_text).should("have.text", acm_constants.south.client_resources_section_i_panel_headers[panelIndex]);
    // Confirm each link point text
    cy.wrap($ele).find(cy_selectors.south.client_resources_panel_links).each(($ele, linkIndex) => {
      const linkArray = acm_constants.south[`client_resources_section_i_panel_header_${panelIndex + 1}_links`];
      cy.wrap($ele).should("have.text", linkArray[linkIndex]);
    });
    cy.wait(1500);
  });
}
